import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  MessageSquare, 
  Bell, 
  Users, 
  Send, 
  Clock, 
  CheckCircle, 
  ArrowLeft,
  Trash2,
  Search,
  Mail,
  User,
  Shield,
  Phone,
  MessageCircle,
  UserCircle,
  Headphones
} from 'lucide-react';
import { userAPI, adminAPI } from '../services/api';
import './MessageCenter.css';

const MessageCenter = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('messages');
  const [messages, setMessages] = useState([]);
  const [supportChat, setSupportChat] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedMessage, setSelectedMessage] = useState(null);
  const [supportStaff, setSupportStaff] = useState([]);
  const [selectedSupport, setSelectedSupport] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // 如果是客服账户，加载客户对话列表
  const [customerChats, setCustomerChats] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  useEffect(() => {
    loadMessages();
    loadSupportStaff();
    if (user?.role === 'support' || user?.role === 'admin') {
      loadCustomerChats();
    } else {
      loadSupportChat();
    }
  }, [user]);

  // 加载接收到的消息
  const loadMessages = async () => {
    try {
      setIsLoading(true);
      const response = await userAPI.getMessages();
      console.log('加载的消息数据:', response);

      if (response.content) {
        setMessages(response.content);
      } else if (Array.isArray(response)) {
        setMessages(response);
      } else {
        setMessages([]);
      }
    } catch (error) {
      console.error('加载消息失败:', error);
      setMessages([]);
    } finally {
      setIsLoading(false);
    }
  };

  // 加载客服人员列表
  const loadSupportStaff = async () => {
    try {
      // 这里调用后端API获取客服人员列表
      const response = await userAPI.getSupportStaff();
      setSupportStaff(response || []);
    } catch (error) {
      console.error('加载客服人员失败:', error);
      // 如果API不存在，使用模拟数据
      setSupportStaff([
        { id: 1, username: '客服小王', email: 'wang@service.com', status: 'online' },
        { id: 2, username: '客服小李', email: 'li@service.com', status: 'busy' },
        { id: 3, username: '客服小张', email: 'zhang@service.com', status: 'offline' }
      ]);
    }
  };

  // 加载客服对话
  const loadSupportChat = async () => {
    try {
      const response = await userAPI.getSupportChat();
      console.log('客服对话数据:', response);
      setSupportChat(Array.isArray(response) ? response : []);
    } catch (error) {
      console.error('加载客服对话失败:', error);
      setSupportChat([]);
    }
  };

  // 客服加载客户对话列表
  const loadCustomerChats = async () => {
    try {
      // 调用客服专用API获取客户对话
      const response = await adminAPI.getCustomerChats();
      setCustomerChats(response || []);
    } catch (error) {
      console.error('加载客户对话失败:', error);
      setCustomerChats([]);
    }
  };

  // 发送消息给客服
  const sendToSupport = async () => {
    if (!newMessage.trim()) return;

    try {
      setIsLoading(true);

      await userAPI.sendToSupport({
        content: newMessage,
        supportId: selectedSupport?.id || null
      });

      const newChatMessage = {
        id: Date.now(),
        messageType: 'SUPPORT',
        content: newMessage,
        createdAt: new Date().toISOString(),
        fromUserId: user.id,
        fromUser: user,
        isRead: true
      };

      setSupportChat(prev => [...prev, newChatMessage]);
      setNewMessage('');

      setTimeout(loadSupportChat, 1000);

    } catch (error) {
      console.error('发送消息失败:', error);
      alert('发送消息失败: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 客服回复客户
  const replyToCustomer = async () => {
    if (!newMessage.trim() || !selectedCustomer) return;

    try {
      setIsLoading(true);

      await adminAPI.replyToCustomer({
        customerId: selectedCustomer.id,
        content: newMessage
      });

      setNewMessage('');
      loadCustomerChats();

    } catch (error) {
      console.error('回复客户失败:', error);
      alert('回复失败: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 标记消息为已读
  const markAsRead = async (messageId) => {
    try {
      await userAPI.markMessageAsRead(messageId);
      setMessages(prev => prev.map(msg => 
        msg.id === messageId ? { ...msg, isRead: true } : msg
      ));
    } catch (error) {
      console.error('标记已读失败:', error);
    }
  };

  // 删除消息
  const deleteMessage = async (messageId) => {
    if (!window.confirm('确定要删除这条消息吗？')) return;

    try {
      await userAPI.deleteMessage(messageId);
      setMessages(prev => prev.filter(msg => msg.id !== messageId));
      if (selectedMessage?.id === messageId) {
        setSelectedMessage(null);
      }
    } catch (error) {
      console.error('删除消息失败:', error);
      alert('删除消息失败');
    }
  };

  // 格式化时间
  const formatTime = (timestamp) => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;

    if (diff < 24 * 60 * 60 * 1000) { // 24小时内
      return date.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });
    } else if (diff < 7 * 24 * 60 * 60 * 1000) { // 7天内
      return date.toLocaleDateString('zh-CN', { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' });
    } else {
      return date.toLocaleDateString('zh-CN');
    }
  };

  // 获取发信人信息
  const getSenderInfo = (message) => {
    if (message.fromUser) {
      return {
        name: message.fromUser.username || '用户',
        avatar: message.fromUser.username?.charAt(0) || 'U',
        role: message.fromUser.role || 'user'
      };
    }

    // 根据消息类型推断
    if (message.messageType === 'BROADCAST') {
      return { name: '系统管理员', avatar: 'S', role: 'admin' };
    } else if (message.messageType === 'SUPPORT') {
      return { name: '客服团队', avatar: 'C', role: 'support' };
    } else {
      return { name: '系统', avatar: 'S', role: 'system' };
    }
  };

  // 获取未读消息数量
  const unreadCount = messages.filter(msg => !msg.isRead).length;

  // 过滤消息
  const filteredMessages = messages.filter(msg => 
    !searchTerm || 
    msg.content?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    msg.subject?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="message-center">
      <div className="message-center-header">
        <div className="header-left">
          <div className="header-icon">
            <MessageSquare size={28} />
          </div>
          <div className="header-text">
            <h1>消息中心</h1>
            <p>查看系统消息和客服对话</p>
          </div>
        </div>
        <button className="back-btn" onClick={() => navigate('/dashboard')}>
          <ArrowLeft size={20} />
          返回主页
        </button>
      </div>

      <div className="message-center-content">
        {/* 标签导航 */}
        <div className="tabs-header">
          <button 
            className={`tab-btn ${activeTab === 'messages' ? 'active' : ''}`}
            onClick={() => setActiveTab('messages')}
          >
            <Bell size={20} />
            <span>系统消息</span>
            {unreadCount > 0 && <span className="badge">{unreadCount}</span>}
          </button>

          {(user?.role === 'support' || user?.role === 'admin') ? (
            <button 
              className={`tab-btn ${activeTab === 'support-desk' ? 'active' : ''}`}
              onClick={() => setActiveTab('support-desk')}
            >
              <Headphones size={20} />
              <span>客服工作台</span>
            </button>
          ) : (
            <button 
              className={`tab-btn ${activeTab === 'support' ? 'active' : ''}`}
              onClick={() => setActiveTab('support')}
            >
              <MessageSquare size={20} />
              <span>客服对话</span>
            </button>
          )}
        </div>

        {/* 系统消息 */}
        {activeTab === 'messages' && (
          <div className="tab-content">
            <div className="content-header">
              <div className="search-bar">
                <Search size={18} />
                <input
                  type="text"
                  placeholder="搜索消息..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>

            {isLoading ? (
              <div className="loading-state">
                <div className="loading-spinner"></div>
                <span>加载消息中...</span>
              </div>
            ) : filteredMessages.length === 0 ? (
              <div className="empty-state">
                <Bell size={48} />
                <h3>暂无消息</h3>
                <p>您还没有收到任何系统消息</p>
              </div>
            ) : (
              <div className="messages-list">
                {filteredMessages.map(message => {
                  const senderInfo = getSenderInfo(message);
                  return (
                    <div 
                      key={message.id} 
                      className={`message-item ${!message.isRead ? 'unread' : ''}`}
                    >
                      <div className="message-avatar">
                        <div className={`avatar ${senderInfo.role}`}>
                          {senderInfo.avatar}
                        </div>
                        <div className="sender-info">
                          <div className="sender-name">{senderInfo.name}</div>
                          <div className="sender-role">
                            {senderInfo.role === 'admin' && <Shield size={12} />}
                            {senderInfo.role === 'support' && <Headphones size={12} />}
                            {senderInfo.role === 'user' && <User size={12} />}
                            {senderInfo.role === 'system' && <Bell size={12} />}
                            <span>
                              {senderInfo.role === 'admin' ? '管理员' : 
                               senderInfo.role === 'support' ? '客服' : 
                               senderInfo.role === 'user' ? '用户' : '系统'}
                            </span>
                          </div>
                        </div>
                      </div>

                      <div className="message-main">
                        <div className="message-header">
                          <h4 className="message-title">
                            {message.subject || '系统通知'}
                          </h4>
                          <div className="message-meta">
                            <span className="message-time">
                              <Clock size={14} />
                              {formatTime(message.createdAt)}
                            </span>
                            <span className={`message-status ${message.isRead ? 'read' : 'unread'}`}>
                              {message.isRead ? (
                                <>
                                  <CheckCircle size={14} />
                                  已读
                                </>
                              ) : (
                                <>
                                  <Mail size={14} />
                                  未读
                                </>
                              )}
                            </span>
                          </div>
                        </div>

                        <div className="message-content">
                          {message.content}
                        </div>

                        <div className="message-actions">
                          {!message.isRead && (
                            <button 
                              className="action-btn mark-read"
                              onClick={() => markAsRead(message.id)}
                            >
                              <CheckCircle size={14} />
                              标记已读
                            </button>
                          )}
                          <button 
                            className="action-btn delete"
                            onClick={() => deleteMessage(message.id)}
                          >
                            <Trash2 size={14} />
                            删除
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        )}

        {/* 客服对话（普通用户） */}
        {activeTab === 'support' && user?.role === 'user' && (
          <div className="tab-content">
            <div className="support-section">
              {/* 客服选择 */}
              <div className="support-selection">
                <h3>选择客服</h3>
                <div className="support-staff-list">
                  {supportStaff.map(staff => (
                    <div 
                      key={staff.id}
                      className={`support-staff-item ${selectedSupport?.id === staff.id ? 'selected' : ''}`}
                      onClick={() => setSelectedSupport(staff)}
                    >
                      <div className="staff-avatar">
                        {staff.username.charAt(0)}
                      </div>
                      <div className="staff-info">
                        <div className="staff-name">{staff.username}</div>
                        <div className={`staff-status ${staff.status}`}>
                          <div className="status-dot"></div>
                          {staff.status === 'online' ? '在线' : 
                           staff.status === 'busy' ? '忙碌' : '离线'}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* 对话区域 */}
              <div className="chat-section">
                <div className="chat-header">
                  <MessageCircle size={20} />
                  <span>
                    {selectedSupport ? `与 ${selectedSupport.username} 对话` : '客服对话'}
                  </span>
                </div>

                <div className="chat-messages">
                  {supportChat.length === 0 ? (
                    <div className="empty-state">
                      <MessageSquare size={48} />
                      <h3>开始与客服对话</h3>
                      <p>选择客服人员，开始您的咨询</p>
                    </div>
                  ) : (
                    supportChat.map(msg => (
                      <div 
                        key={msg.id} 
                        className={`chat-message ${msg.fromUserId === user.id ? 'user' : 'support'}`}
                      >
                        <div className="message-avatar">
                          <div className={`avatar ${msg.fromUserId === user.id ? 'user' : 'support'}`}>
                            {msg.fromUserId === user.id ? 
                              (user?.username?.charAt(0) || 'U') : 
                              'C'
                            }
                          </div>
                        </div>
                        <div className="message-bubble">
                          <div className="message-text">{msg.content}</div>
                          <div className="message-timestamp">
                            {formatTime(msg.createdAt)}
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>

                <div className="chat-input-section">
                  <textarea
                    className="chat-input"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder={selectedSupport ? `向 ${selectedSupport.username} 发送消息...` : "输入您的问题或建议..."}
                    rows="3"
                    disabled={isLoading}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendToSupport();
                      }
                    }}
                  />
                  <button 
                    className="send-btn"
                    onClick={sendToSupport}
                    disabled={!newMessage.trim() || isLoading}
                  >
                    <Send size={18} />
                    {isLoading ? '发送中...' : '发送'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* 客服工作台 */}
        {activeTab === 'support-desk' && (user?.role === 'support' || user?.role === 'admin') && (
          <div className="tab-content">
            <div className="support-desk">
              <div className="desk-sidebar">
                <h3>客户列表</h3>
                <div className="customer-list">
                  {customerChats.map(chat => (
                    <div 
                      key={chat.id}
                      className={`customer-item ${selectedCustomer?.id === chat.id ? 'selected' : ''}`}
                      onClick={() => setSelectedCustomer(chat)}
                    >
                      <div className="customer-avatar">
                        <UserCircle size={32} />
                      </div>
                      <div className="customer-info">
                        <div className="customer-name">{chat.customerName}</div>
                        <div className="last-message">
                          {chat.lastMessage || '暂无消息'}
                        </div>
                        <div className="message-time">
                          {formatTime(chat.updatedAt)}
                        </div>
                      </div>
                      {chat.unreadCount > 0 && (
                        <span className="unread-badge">{chat.unreadCount}</span>
                      )}
                    </div>
                  ))}
                </div>
              </div>

              <div className="desk-main">
                {selectedCustomer ? (
                  <>
                    <div className="chat-header">
                      <UserCircle size={24} />
                      <span>与 {selectedCustomer.customerName} 的对话</span>
                    </div>

                    <div className="chat-messages">
                      {selectedCustomer.messages?.map(msg => (
                        <div 
                          key={msg.id} 
                          className={`chat-message ${msg.isFromCustomer ? 'customer' : 'support'}`}
                        >
                          <div className="message-bubble">
                            <div className="message-text">{msg.content}</div>
                            <div className="message-timestamp">
                              {formatTime(msg.createdAt)}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>

                    <div className="chat-input-section">
                      <textarea
                        className="chat-input"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="回复客户..."
                        rows="3"
                        disabled={isLoading}
                      />
                      <button 
                        className="send-btn"
                        onClick={replyToCustomer}
                        disabled={!newMessage.trim() || isLoading}
                      >
                        <Send size={18} />
                        回复
                      </button>
                    </div>
                  </>
                ) : (
                  <div className="empty-state">
                    <Users size={48} />
                    <h3>选择客户</h3>
                    <p>从左侧选择客户开始对话</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MessageCenter;
